definition.script:
pipeline {
  agent any

  tools {nodejs "node"}

  stages {

    stage('run tests') {
      steps {
        git branch: 'front-jenkins', url: "https://github.com/Elly-G/IntellST.git"
        dir("intellst-front/output/coverage/jest/") {
        sh 'pwd'
        sh 'ls'
        sh 'npm install'
        sh 'npm run test'
        sh 'pwd'
        }
        step([
            $class:'CloverPublisher',
            cloverReportDir: '/var/jenkins_home/workspace/Front-Test-Coverage/intellst-front/output/coverage/jest',
            cloverReportFileName: 'clover.xml',
            healthyTarget: [methodCoverage: 70, conditionalCoverage: 70, statementCoverage: 70],
            unhealthyTarget: [methodCoverage: 50, conditionalCoverage: 50, statementCoverage: 50],
            failingTarget: [methodCoverage: 0, conditionalCoverage: 0, statementCoverage: 0]
            ])
      }
    }
  }
}
