definition.script:
pipeline {
  agent any

  tools {nodejs "node"}

  stages {

    stage('run tests') {
      steps {
        dir("intellst-front") {
        sh 'pwd'
        sh 'ls'
        sh 'npm install'
        sh 'npm run test:unit'
        sh 'ls'
        }
      }
    }
    stage('run report with jest'){
        steps {
             dir("intellst-front/output/coverage/jest/") {
        sh 'pwd'
        sh 'ls'
        }
        }
    }
  }
}
